package medic.esy.es.mamyapp.Activites.parent;import android.net.Uri;import android.os.AsyncTask;import android.os.Bundle;import android.support.annotation.NonNull;import android.support.v4.app.Fragment;import android.view.LayoutInflater;import android.view.View;import android.view.ViewGroup;import android.widget.Toast;import com.google.firebase.database.DataSnapshot;import com.google.firebase.database.DatabaseError;import com.google.firebase.database.DatabaseReference;import com.google.firebase.database.FirebaseDatabase;import com.google.firebase.database.ValueEventListener;import cn.jzvd.Jzvd;import cn.jzvd.JzvdStd;import medic.esy.es.mamyapp.Activites.model.commonParent;import medic.esy.es.mamyapp.R;public class childVideos extends Fragment   {    JzvdStd jzvdStd;    @Override    public View onCreateView(LayoutInflater inflater, ViewGroup container,                             Bundle savedInstanceState) {       View root=inflater.inflate(R.layout.fragment_child_videos, container, false);        jzvdStd = (JzvdStd) root.findViewById(R.id.videoplayer);        getVideos getVideos=new getVideos();        getVideos.execute();        return root;    }    public class getVideos extends AsyncTask<Void,Void,Void>{        @Override        protected Void doInBackground(Void... voids) {            String babyphone =commonParent.currentuser.getPhone();            DatabaseReference mDatabase = FirebaseDatabase.getInstance().getReference().child("childernVideo").child(commonParent.currentuser.getPhone()).child("mImageUrl");            mDatabase.keepSynced(true);            mDatabase.addValueEventListener(new ValueEventListener() {                @Override                public void onDataChange(@NonNull DataSnapshot dataSnapshot) {                    if (dataSnapshot.getValue() != null) {                        jzvdStd.setUp(dataSnapshot.getValue().toString()                                , "Your Child Video" , Jzvd.SCREEN_WINDOW_NORMAL);                    }                    else {                        Toast.makeText(getActivity(),"not uploaded video Available now !",Toast.LENGTH_SHORT).show();                    }                }                @Override                public void onCancelled(@NonNull DatabaseError databaseError) {                    Toast.makeText(getActivity(),"faild",Toast.LENGTH_SHORT).show();                }            });            return null;        }    }}